import React, {useEffect, useState} from 'react';
import {Link, RouteComponentProps, useParams} from 'react-router-dom';
import {useDialog} from "../../context/DialogContext";


const ConfirmDialog: any = ({title, content, confirm, cancel}) => {

    const { closeDialog } = useDialog();

    const confirm_color = confirm?.color ?? 'white';
    const confirm_background = confirm?.background ?? 'indigo';
    const confirm_text = confirm?.text ?? 'No text';
    const confirm_onclick = () => {
        closeDialog();
        confirm?.onclick();
    };

    const cancel_color = cancel?.color ?? 'gray';
    const cancel_background = cancel?.background ?? 'white';
    const cancel_text = cancel?.text ?? 'No text';
    const cancel_onclick = () => {
        closeDialog();
        cancel?.onclick();
    };

    return (
        <>
            <div className="relative z-10 opacity-100" aria-labelledby="modal-title" role="dialog" aria-modal="true">
                <div className="fixed inset-0 bg-gray-500 bg-opacity-75 transition-opacity"></div>

                <div className="opacity-100 translate-y-0 sm:scale-100 fixed inset-0 z-10 w-screen overflow-y-auto">
                    <div className="flex min-h-full items-end justify-center p-4 text-center sm:items-center sm:p-0">
                        <div
                            className="relative transform overflow-hidden rounded-lg bg-white text-left shadow-xl transition-all sm:my-8 sm:w-full sm:max-w-lg">
                            <div className="bg-white px-4 pb-4 pt-5 sm:p-6 sm:pb-4">
                                <div className="sm:flex sm:items-start">
                                    <div className="mt-3 text-center sm:ml-4 sm:mt-0 sm:text-left">
                                        <h3 className="text-base font-semibold leading-6 text-gray-900"
                                            id="modal-title">
                                            {title ?? 'Untitled'}
                                        </h3>
                                        <div className="mt-2">
                                            <p className="text-sm text-gray-500">
                                                {content ?? 'No content provided.'}
                                            </p>
                                        </div>
                                    </div>
                                </div>
                            </div>
                            <div className="bg-gray-50 px-4 py-3 sm:flex sm:flex-row-reverse sm:px-6">
                                <button onClick={confirm_onclick} type="button"
                                        className={"inline-flex w-full justify-center rounded-md bg-" + confirm_background + "-600 px-3 py-2 text-sm font-semibold text-" + confirm_color + " shadow-sm hover:bg-" + confirm_background + "-500 sm:ml-3 sm:w-auto"}>
                                    {confirm_text}
                                </button>
                                <button onClick={cancel_onclick} type="button"
                                        className={"mt-3 inline-flex w-full justify-center rounded-md bg-" + cancel_background + " px-3 py-2 text-sm font-semibold text-" + cancel_color + "-900 shadow-sm ring-1 ring-inset ring-gray-300 hover:bg-gray-50 sm:mt-0 sm:w-auto"}>
                                    {cancel_text}
                                </button>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </>
    )
}

export default ConfirmDialog;
